import React, { useState } from 'react';
import InputBox from './InputBox';
import ToDoGrid from './ToDoGrid';
import ToggleMessage from './ToggleMessage';


const ToDoList = () => {


    const [ listState, setListState ] = useState({});

    const onAddTask = (date, toDo) => {

        const stateCopy = {...listState};

        if (stateCopy[date]) {
            stateCopy[date].push(toDo);
        } else {
            stateCopy[date] = [toDo];
        };

        setListState(stateCopy);
    };


    return (
        <div>
            <InputBox onAddTask={onAddTask} />
            {listState = {} ? <ToggleMessage message="Your calendar is empty!" isOn={true} /> :  
            <ToDoGrid listState={listState} />}
        </div>
    );
}

export default ToDoList;
