import React from 'react';

const TaskItem = ({ task, isCompleted, completeTask }) => {


    return (
        <li>
            {task}
            {isCompleted ? 
            <button completeTask={() => completeTask()}>o</button> : 
            <button completeTask={() => completeTask()}>x</button>
            }
        </li>
    );
}

export default TaskItem;
