import React from 'react';

const ToDoGrid = ({ listState }) => {


    const mapList = () => {
        for (const [ date, taskArray ] of Object.entries(listState)) {
            <div>
                <h1>{date}</h1>
                <ul>
                    {mapTasks(taskArray)}
                </ul>
            </div>
        };
    };

    const mapTasks = ( taskArray ) => {
        return taskArray.map((task, index) => <li key={index}>{task}</li>)
    }

    return (
        <>
            {mapList()}
        </>
    );
}

export default ToDoGrid;
